import React, { useState, useEffect } from "react";
import axios from "axios";
import config from "../config.json";
import "./Profile.css";
import {Image, Button, Card, Col, Row } from "react-bootstrap";

const Profile = () => {
    const [profilePlayList, setProfilePlayList] = useState([]);
    const [profileId, setProfileId] = useState("");
    /* const [profileImage, setProfileImage] =useState(""); */
    const [profileEmail, setProfileEmail] = useState("");
    const [profileName, setProfileName] = useState("");

    const profileData = async () => {
        try {
            /* const res = await axios.get("http://localhost:4000/user/profile"); */
            const res = await axios.get(config.api.url + "/user/profile", {
                headers: { Authorization: localStorage.getItem("jtoken") },
            });
            console.log(res);
            setProfilePlayList(res.data.profile.playlists);

            setProfileEmail(res.data.profile.user_email);
            setProfileName(res.data.profile.user_name);
            setProfileId(res.data.profile._id);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        profileData();
    }, []);

    return (
        <div className="profile-container">
            {window.location.search &&
                new URLSearchParams(window.location.search) &&
                new URLSearchParams(window.location.search).get("jtoken") &&
                localStorage.setItem(
                    "jtoken",
                    new URLSearchParams(window.location.search).get("jtoken")
                )}
            {!localStorage.getItem("jtoken")
                ? (window.location.href = "/login")
                : null}

            {/* <div className="profile-card">
                <Row>
                    <Col>
                        <h1 className="profile-name">{`UserName - ${profileName}`}</h1>
                    </Col>
                </Row>

                <Row>
                    <Col>
                        <p className="profile-id">{`UserId - ${profileId}`}</p>
                    </Col>
                </Row>

                <Row>
                    <Col>
                        <p className="profile-email">{`UserEmail - ${profileEmail}`}</p>
                    </Col>
                </Row>

                <Row>
                    <Col>
                        <p>
                            {profilePlayList.map((value) => (
                                <div className="profile-playlist">
                                    <p>{value.name}</p>
                                </div>
                            ))}
                        </p>
                    </Col>
                </Row>
            </div> */}


        <Row>
            <Col></Col>
            <Col>
            <Card style={{ width: "18rem" }}>
                {/* <Card.Img variant="top" src="holder.js/100px180" /> */}
                <Col xs={6} md={4}>
                    <Image variant="top" src="https://static.toiimg.com/thumb/resizemode-4,msid-76729750,imgsize-249247,width-720/76729750.jpg/100p" roundedCircle />
                </Col>
                <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and
                        make up the bulk of the card's content.
                    </Card.Text>

                </Card.Body>
            </Card>
            </Col>
            <Col></Col>
        </Row>
        </div>
    );
};

export default Profile;
